import { Component} from '@angular/core';
import { Product, products } from '../products';
import { categoryId } from 'src/app/product-items/product-items.component'; 

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {
  products = products;
  deleted: number[]=[];

  share(product: Product) {
    const url=product.link;
    window.open("https://api.whatsapp.com/send?text=" + url);
  }
  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
  filteredProducts(){ 
    return this.products.filter(p => p.categoryId === categoryId && !this.deleted.find(deletedId=> deletedId === p.categoryId)); 
  } 
  pressLike(product: Product){ 
    product.likecnt++;
  } 
  deleteProduct(product: Product){
    this.deleted.push(product.id);
  }
}


